        style={{ width: 70, marginLeft: 10, marginRight: 10 }}
        onChange={(e) => setNeuron(parseInt(e.target.value))}

496.content:
                className="m-2 text-blue-500 hover:text-blue-700"
                title={description}
              >
                {label} ({layer}:{neuron})
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  )
}
export default NeuronForm
